const Task = ({ task, onDelete, onToggle }) => {
  return (
    <div className='task' onDoubleClick={() => onToggle(id)}>
        <h3>{task.text}<FaTimes 
        style={{ color:'red', cursor: 'pointer'}} 
        onClick={() => onDelete(task.id)}/></h3>
        <p>{task.day}</p>

    </div>
  )
}

export default Task
